  function colourInput(dialog, v) {
    return {
      preferredFormat: "hex",
      color: dialog.component.data[v],
      showAlpha: false,
      showInput: true,
      move: c => updateColour(dialog, v, c),
      change: c => updateColour(dialog, v, c),
      hide: c => updateColour(dialog, v, c)
    }
  }

